    @Override
    public ResponseEntity<byte[]> printReturnRequestForm(final String returnNumber, final String orderNumber,
            final String orderShippingDate, final List<SSLReturnEntryDTO> list, final SSLWarehouseAddressDTO warehouse) {
        StringReader sr = null;
        try {
            final String pdfFileName = String.format("%s-%s%s", PdfConstants.RETURNREQUEST, returnNumber, PdfConstants.PDFEXTENSION);

            final Document document = new Document(PageSize.A4);
            final ByteArrayOutputStream baos = new ByteArrayOutputStream();
            final PdfWriter pdfWriter = PdfWriter.getInstance(document, baos);

            final VelocityContext velocityContext = new VelocityContext();
            velocityContext.put(PdfConstants.RETURNNUMBER, returnNumber.toCharArray());
            velocityContext.put(PdfConstants.ORDERNUMBER, orderNumber.toCharArray());
            velocityContext.put(PdfConstants.ORDERDATE, orderShippingDate.toCharArray());
            velocityContext.put(PdfConstants.ENTRIES, list);
            velocityContext.put(PdfConstants.WAREHOUSE, warehouse);

            final HeaderFooterPageEvent event = new HeaderFooterPageEvent();
            pdfWriter.setPageEvent(event);
            document.setMargins(PdfConstants.MARGIN_ZERO, PdfConstants.MARGIN_ZERO, 90.0f, PdfConstants.MARGIN_ZERO);
            document.open();

            sr = new StringReader(getSslUtilityForPDF().mergeVmTemplate(
                    String.format("%s%s%s", PdfConstants.VMPATH, PdfConstants.RETURNREQUESTFILE, PdfConstants.VMEXTENSION),
                    velocityContext));
            final float fontSize = 12.0f;
            final XMLWorker worker = new XMLWorker(getSslUtilityForPDF().getPipeline(document, pdfWriter, fontSize), true);
            final XMLParser p = new XMLParser(worker);
            p.parse(sr);
            document.close();

            final Document documentWithFooter = new Document(PageSize.A4);
            documentWithFooter.setMargins(PdfConstants.MARGIN_ZERO, PdfConstants.MARGIN_ZERO, 90.0f, PdfConstants.MARGIN_ZERO);
            final PdfWriter writer = PdfWriter.getInstance(documentWithFooter, new FileOutputStream(pdfFileName));
            documentWithFooter.open();
            final PdfContentByte cb = writer.getDirectContent();

            // Load Created PDF to add footer on last page
            final PdfReader reader = new PdfReader(baos.toByteArray());
            final int n = reader.getNumberOfPages();
            for (int i = 0; i < n - 1; i++) {
                final PdfImportedPage page = writer.getImportedPage(reader, i + 1);
                // Copy pages of existing PDF into output PDF
                documentWithFooter.newPage();
                cb.addTemplate(page, 0, 0);
            }

            final PdfImportedPage page = writer.getImportedPage(reader, n);
            // Copy last page of existing PDF into output PDF
            documentWithFooter.setMargins(PdfConstants.MARGIN_ZERO, PdfConstants.MARGIN_ZERO, 90.0f, 200.0f);
            documentWithFooter.newPage();
            cb.addTemplate(page, 0, 0);

            // Add Footer
            final PdfContentByte cbFooter = writer.getDirectContent();
            final byte[] imgFooter = Base64.decode(
                    "");
            final Image imageFooter = Image.getInstance(imgFooter);
            imageFooter.scaleToFit(580, 350);
            imageFooter.setAbsolutePosition(8, 150);
            writer.getDirectContent().addImage(imageFooter);

            final Font fontBold = new Font();
            fontBold.setSize(12.0F);
            fontBold.setStyle(Font.BOLD);
            ColumnText.showTextAligned(cbFooter, Element.ALIGN_LEFT, new Phrase("To: " + warehouse.getName(), fontBold), 10, 130, 0);
            final Font fontNormal = new Font();
            fontNormal.setSize(12.0F);
            fontNormal.setStyle(Font.NORMAL);
            ColumnText.showTextAligned(cbFooter, Element.ALIGN_LEFT, new Phrase(warehouse.getLine1() + ",", fontNormal), 10, 112, 0);
            ColumnText.showTextAligned(cbFooter, Element.ALIGN_LEFT, new Phrase(warehouse.getLine2() + ",", fontNormal), 10, 94, 0);
            ColumnText.showTextAligned(cbFooter, Element.ALIGN_LEFT,
                    new Phrase(warehouse.getDistrict() + ", " + warehouse.getCity() + " - " + warehouse.getPinCode() + ",", fontNormal), 10,
                    76, 0);
            ColumnText.showTextAligned(cbFooter, Element.ALIGN_LEFT, new Phrase(warehouse.getState(), fontNormal), 10, 58, 0);
            ColumnText.showTextAligned(cbFooter, Element.ALIGN_LEFT, new Phrase("Contact No : " + warehouse.getContact(), fontNormal), 10,
                    35, 0);
            ColumnText.showTextAligned(cbFooter, Element.ALIGN_LEFT, new Phrase("TOLL FREE #" + warehouse.getTollFree(), fontNormal), 10,
                    15, 0);

            documentWithFooter.close();
            return getSslUtilityForPDF().getPdfFileResponse(pdfFileName, pdfFileName);
        } catch (final RuntimeWorkerException rwe) {
            if (sr != null) {
                LOG.info(sr.toString());
            }
            LOG.error("HTML Parser error with incorrect content.  Please see log above for HTML", rwe);
        } catch (final Exception ex) {
            LOG.error("Error generating Form", ex);
        }

        return null;
    }

    class HeaderFooterPageEvent extends PdfPageEventHelper {
        @Override
        public void onEndPage(final PdfWriter writer, final Document document) {
            try {
                final byte[] imgHeader = Base64.decode(
                        "");
                final Image imageHeader = Image.getInstance(imgHeader);
                imageHeader.scaleToFit(595, 400);
                imageHeader.setAbsolutePosition(0, 760);
                writer.getDirectContent().addImage(imageHeader);
            } catch (final IOException | DocumentException e) {
                LOG.error("Error in adding Header to Return Slip PDF");
                LOG.error(e.getStackTrace());
            }
        }
    }

