    @Override
    public XSSFWorkbook getExcelForReturn(final SearchRMARequest searchRMARequest, final String stockLocationCode) {
        searchRMARequest.setPageNumber(null);
        searchRMARequest.setRowsPerPage(null);
        final List<ReturnRequestModel> returnRequests = getSslODCReturnDao().search(searchRMARequest, stockLocationCode, RMA_PANEL)
                .getValue();
        // Create a new Workbook
        final XSSFWorkbook workbook = new XSSFWorkbook();

        final DateFormat df = new SimpleDateFormat("DDMMYY_HHMMSS");
        // Create a blank sheet
        final XSSFSheet sheet = workbook
                .createSheet(String.format("%s-%s_%s", ExcelConstants.EXCEL_NAME, stockLocationCode, df.format(new Date())));

        // Create the data for the excel sheet
        final Map<String, Object[]> data = new TreeMap<String, Object[]>();
        Integer rownum = 1;
        data.put(rownum.toString(), ExcelConstants.HEADER);
        if (CollectionUtils.isNotEmpty(returnRequests)) {
            for (final ReturnRequestModel returnRequest : returnRequests) {
                try {
                    final ClientRMADataResponse response = getSslDefaultRMAConverter().convert(returnRequest);
                    getSslDefaultRMAExtensionConverter().convert(returnRequest, response);
                    response.setConsignee(addressConverter.convert(returnRequest.getOrder().getDeliveryAddress()));
                    rownum++;
                    String orderNumber = StringUtils.EMPTY;
                    String orderDate = StringUtils.EMPTY;
                    String awbNumber = StringUtils.EMPTY;
                    String rmaNumber = StringUtils.EMPTY;
                    String carrier = StringUtils.EMPTY;
                    String packageValue = StringUtils.EMPTY;
                    String consigneeFirstName = StringUtils.EMPTY;
                    String consigneeLastName = StringUtils.EMPTY;
                    String consigneeLine1 = StringUtils.EMPTY;
                    String consigneeLine2 = StringUtils.EMPTY;
                    String consigneePin = StringUtils.EMPTY;
                    String consigneeCity = StringUtils.EMPTY;
                    String consigneeState = StringUtils.EMPTY;
                    String consigneePhone = StringUtils.EMPTY;
                    String warehouseFirstName = StringUtils.EMPTY;
                    String warehouseLastName = StringUtils.EMPTY;
                    String warehouseLine1 = StringUtils.EMPTY;
                    String warehouseLine2 = StringUtils.EMPTY;
                    String warehousePin = StringUtils.EMPTY;
                    String warehouseCity = StringUtils.EMPTY;
                    String warehouseState = StringUtils.EMPTY;
                    String quantity = StringUtils.EMPTY;
                    String status = StringUtils.EMPTY;
                    String description = StringUtils.EMPTY;
                    String paymentType = StringUtils.EMPTY;
                    String pickupDate = StringUtils.EMPTY;
                    if (null != response.getRma()) {
                        rmaNumber = response.getRma();
                    }
                    if (null != response.getAwb()) {
                        awbNumber = response.getAwb();
                    }
                    if (null != response.getCourier()) {
                        carrier = response.getCourier();
                    }
                    if (null != response.getOrderData().getOrderNumber()) {
                        orderNumber = response.getOrderData().getOrderNumber();
                    }
                    if (null != response.getOrderData().getOrderDate()) {
                        orderDate = response.getOrderData().getOrderDate();
                    }
                    if (0 != response.getRefundAmount()) {
                        packageValue = String.valueOf(response.getRefundAmount());
                    }
                    if (StringUtils.isNotEmpty(response.getConsignee().getFirstName())) {
                        consigneeFirstName = response.getConsignee().getFirstName();
                    }
                    if (StringUtils.isNotEmpty(response.getConsignee().getLastName())) {
                        consigneeLastName = response.getConsignee().getLastName();
                    }
                    if (StringUtils.isNotEmpty(response.getConsignee().getLine1())) {
                        consigneeLine1 = response.getConsignee().getLine1();
                    }
                    if (StringUtils.isNotEmpty(response.getConsignee().getLine2())) {
                        consigneeLine2 = response.getConsignee().getLine2();
                    }
                    if (StringUtils.isNotEmpty(response.getConsignee().getPostalCode())) {
                        consigneePin = response.getConsignee().getPostalCode();
                    }
                    if (StringUtils.isNotEmpty(response.getConsignee().getTown())) {
                        consigneeCity = response.getConsignee().getTown();
                    }
                    if (StringUtils.isNotEmpty(response.getConsignee().getState())) {
                        consigneeState = response.getConsignee().getState();
                    }
                    if (StringUtils.isNotEmpty(response.getConsignee().getPhone())) {
                        consigneePhone = response.getConsignee().getPhone();
                    }
                    if (StringUtils.isNotEmpty(response.getWarehouse().getAddress().getFirstName())) {
                        warehouseFirstName = response.getWarehouse().getAddress().getFirstName();
                    }
                    if (StringUtils.isNotEmpty(response.getWarehouse().getAddress().getLastName())) {
                        warehouseLastName = response.getWarehouse().getAddress().getLastName();
                    }
                    if (StringUtils.isNotEmpty(response.getWarehouse().getAddress().getLine1())) {
                        warehouseLine1 = response.getWarehouse().getAddress().getLine1();
                    }
                    if (StringUtils.isNotEmpty(response.getWarehouse().getAddress().getLine2())) {
                        warehouseLine2 = response.getWarehouse().getAddress().getLine2();
                    }
                    if (StringUtils.isNotEmpty(response.getWarehouse().getAddress().getPostalCode())) {
                        warehousePin = response.getWarehouse().getAddress().getPostalCode();
                    }
                    if (StringUtils.isNotEmpty(response.getWarehouse().getAddress().getTown())) {
                        warehouseCity = response.getWarehouse().getAddress().getTown();
                    }
                    if (StringUtils.isNotEmpty(response.getWarehouse().getAddress().getState())) {
                        warehouseState = response.getWarehouse().getAddress().getState();
                    }
                    if (StringUtils.isNotEmpty(response.getQuantity())) {
                        quantity = response.getQuantity();
                    }
                    if (StringUtils.isNotEmpty(response.getDisplayStatus())) {
                        status = response.getDisplayStatus();
                    }
                    if (StringUtils.isNotEmpty(response.getDescription())) {
                        description = response.getDescription();
                    }
                    if (StringUtils.isNotEmpty(response.getOrderData().getPaymentMethod())) {
                        paymentType = response.getOrderData().getPaymentMethod();
                    }
                    if (StringUtils.isNotEmpty(response.getPickupDate())) {
                        pickupDate = response.getPickupDate();
                    }
                    final Object[] sheetRow = { rmaNumber, awbNumber, orderNumber, orderDate, carrier, quantity, status, pickupDate,
                            packageValue, consigneeFirstName, consigneeLastName, consigneeLine1, consigneeLine2, consigneePin,
                            consigneeCity, consigneeState, consigneePhone, description, paymentType,
                            warehouseFirstName + "\n" + warehouseLastName + "\n" + warehouseLine1 + ", " + warehouseLine2 + ", "
                                    + warehouseCity + "\n" + warehousePin + "\n" + warehouseCity + "\n" + warehouseState };
                    data.put(rownum.toString(), sheetRow);
                } catch (final Exception ex) {
                    LOG.error("Delivery Address is missing", ex);
                }

            }
        }
        // Iterate over data and write it to the sheet
        final Set keyset = data.keySet();
        rownum = 0;
        for (final Object key : keyset) {
            final Row row = sheet.createRow(rownum++);
            final Object[] objArr = data.get(key);
            int cellnum = 0;
            for (final Object obj : objArr) {
                final Cell cell = row.createCell(cellnum++);
                if (obj instanceof String) {
                    cell.setCellValue((String) obj);
                } else if (obj instanceof Double) {
                    cell.setCellValue((Double) obj);
                }
            }
        }
        for (int i = 0; i < 20; i++) {
            sheet.autoSizeColumn(i);
        }
        return workbook;
    }

    @Override
    public String validateHeaders(final String[] allHeaders) {
        final CourierRMAUploadHeader[] csvHeaders = CourierRMAUploadHeader.class.getEnumConstants();
        final String[] csvHeadersString = new String[csvHeaders.length];
        if (ArrayUtils.isNotEmpty(csvHeaders)) {
            int index = 0;
            for (final CourierRMAUploadHeader headerName : csvHeaders) {
                if (null != headerName) {
                    csvHeadersString[index] = headerName.toString();
                }
                index++;
            }
        }
        if (ArrayUtils.isNotEmpty(allHeaders)) {
            for (final String headerName : allHeaders) {
                if (!ArrayUtils.contains(csvHeadersString, headerName.trim().toUpperCase())) {
                    return new StringBuilder("Header Name mismatched for \"").append(headerName).append("\"").toString();
                }
            }
        } else {
            return "No Header found.";
        }

        return null;
    }

    @Override
    public Map<String, Integer> processCSVData(final List<String> csvRowData, final FileWriter fileWriter) throws IOException {
        final Map<String, Integer> result = new HashMap<>();
        if (CollectionUtils.isNotEmpty(csvRowData)) {
            int count = 0;
            int successCount = 0;
            int failureCount = 0;
            for (final String rowData : csvRowData) {
                fileWriter.append(rowData).append(CSVConstants.CSV_DELIMITER);
                if (StringUtils.countMatches(rowData, CSVConstants.CSV_DELIMITER) != 3) {
                    fileWriter.append(SslCoreConstants.NOT_UPDATED).append(SslCoreConstants.NEW_LINE_CHARACTER);
                    count++;
                    failureCount++;
                    continue;
                }
                final String[] data = rowData.split(CSVConstants.CSV_DELIMITER);
                final String rmaNumber = StringUtils.isNotEmpty(data[0]) ? data[0].trim() : StringUtils.EMPTY;
                final String carrier = StringUtils.isNotEmpty(data[1]) ? data[1].trim() : StringUtils.EMPTY;
                final String awbNumber = StringUtils.isNotEmpty(data[2]) ? data[2].trim() : StringUtils.EMPTY;
                String status = StringUtils.EMPTY;
                if (data.length >= 4) {
                    status = StringUtils.isNotEmpty(data[3]) ? data[3].trim() : StringUtils.EMPTY;
                }

                final String resultString = getOdcReturnService().updateRMAForCSVEntry(rmaNumber, carrier.toUpperCase(), awbNumber, status);
                if (resultString.equals(SslCoreConstants.UPDATED)) {
                    fileWriter.append(resultString).append(SslCoreConstants.NEW_LINE_CHARACTER);
                    successCount++;
                } else {
                    fileWriter.append(resultString).append(SslCoreConstants.NEW_LINE_CHARACTER);
                    failureCount++;
                }
                count++;
            }
            result.put("totalCount", Integer.valueOf(count));
            result.put("successCount", Integer.valueOf(successCount));
            result.put("failureCount", Integer.valueOf(failureCount));
        }
        return result;
    }

    @Override
    public Map<String, Integer> uploadCSV(final Scanner scanner) throws IOException {
        fileWriter = getFileWriterObject();
        scanner.useDelimiter(CSVConstants.CSV_DELIMITER);
        int count = 0;
        final List<String> csvRowData = new ArrayList<>();
        Map<String, Integer> result = new HashMap<String, Integer>();
        while (scanner.hasNextLine()) {
            final String lineData = scanner.nextLine();
            if (count == 0) {
                fileWriter.append(lineData).append(CSVConstants.CSV_DELIMITER).append("Processing Status")
                        .append(SslCoreConstants.NEW_LINE_CHARACTER);
                final String[] csvHeader = lineData.split(CSVConstants.CSV_DELIMITER);
                final String error = validateHeaders(csvHeader);
                if (StringUtils.isEmpty(error)) {
                    count++;
                    continue;
                } else {
                    result.put("headerError", count);
                    return result;
                }
            } else {
                csvRowData.add(lineData);
            }
            count++;
        }
        if (count == 1) {
            result.put("noRecordToUpdate", count);
            return result;
        }
        result = processCSVData(csvRowData, fileWriter);
        scanner.close();
        fileWriter.close();
        return result;
    }

