    @Override
    public XSSFWorkbook getSheetForOpenRMAsWithBankDetails(final String stockLocationCode) {
        final List<ReturnRequestModel> openRMAs = getSslODCReturnDao().getOpenRmasForStore(stockLocationCode);
        // Create a new Workbook
        final XSSFWorkbook workbook = new XSSFWorkbook();

        final DateFormat df = new SimpleDateFormat("ddMMyy_HHmmss");
        // Create a blank sheet
        final XSSFSheet sheet = workbook
                .createSheet(String.format("%s-%s_%s", ExcelConstants.OPEN_RMAS_EXCEL_NAME, stockLocationCode, df.format(new Date())));

        // Create the data for the excel sheet
        final Map<String, Object[]> data = new TreeMap<String, Object[]>();
        Integer rownum = 1;
        data.put(rownum.toString(), OpenRMAExcelConstants.HEADER_FOR_OPEN_RMA_SHEET);
        if (CollectionUtils.isNotEmpty(openRMAs)) {
            for (final ReturnRequestModel openRMA : openRMAs) {
                final OrderModel order = openRMA == null ? null : openRMA.getOrder();
                final ReturnRequestBankDetailsModel bankDetails = openRMA == null ? null : openRMA.getBankDetails();
                if (order != null && bankDetails != null) {
                    rownum++;
                    final String rma = openRMA.getCode();
                    final String orderNumber = order.getCode();
                    final String returnStatus = openRMA.getStatus().getCode();
                    final String customerName = order.getUser() == null || StringUtils.isEmpty(order.getUser().getName())
                            ? StringUtils.EMPTY : order.getUser().getName();
                    final String accountName = StringUtils.isEmpty(bankDetails.getAccountName()) ? StringUtils.EMPTY
                            : bankDetails.getAccountName();
                    final String acNumber = StringUtils.isEmpty(bankDetails.getAccountNumber()) ? StringUtils.EMPTY
                            : bankDetails.getAccountNumber();
                    final String ifscCode = StringUtils.isEmpty(bankDetails.getIfscCode()) ? StringUtils.EMPTY : bankDetails.getIfscCode();
                    final String bankName = StringUtils.isEmpty(bankDetails.getBankName()) ? StringUtils.EMPTY : bankDetails.getBankName();

                    final Object[] sheetRow = { rma, orderNumber, returnStatus, customerName, accountName, acNumber, ifscCode, bankName };
                    data.put(rownum.toString(), sheetRow);

                }
            }
        }
        // Iterate over data and write it to the sheet
        final Set keyset = data.keySet();
        rownum = 0;
        for (final Object key : keyset) {
            final Row row = sheet.createRow(rownum++);
            final Object[] objArr = data.get(key);
            int cellnum = 0;
            for (final Object obj : objArr) {
                final Cell cell = row.createCell(cellnum++);
                if (obj instanceof String) {
                    cell.setCellValue((String) obj);
                } else if (obj instanceof Double) {
                    cell.setCellValue((Double) obj);
                }
            }
        }
        for (int i = 0; i < OpenRMAExcelConstants.HEADER_FOR_OPEN_RMA_SHEET.length; i++) {
            sheet.autoSizeColumn(i);
        }
        return workbook;
    }

    private FileWriter getFileWriterObject() throws IOException {
        final File file = new File(CSVConstants.DATA_DIR + CSVConstants.UNRESOLVED_FILE_PATH + CSVConstants.LOG_FILE_NAME);
        file.getParentFile().mkdirs();
        return new FileWriter(file);
    }

